import React, { useState } from 'react';
import './LoginPage.css';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('/api/LoginPage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const result = await response.json();

            if (response.ok) {
                setSuccess('Login successful!');
                setError('');
                // Redirect or perform further actions
            } else {
                setError(result.error || 'Invalid credentials, please try again.');
                setSuccess('');
            }
        } catch (error) {
            setError('Something went wrong, please try again.');
            setSuccess('');
        }
    };

    return (
        <div>
            <nav className="navbar">
                <div className="navbar-content">
                    <button className="navbar-button" onClick={() => window.location.href = '/login'}>
                        Zenith Bank
                    </button>
                    <span className="navbar-text">Don't have an account? </span>
                    <button className="create-user-button" onClick={() => window.location.href = '/create-user'}>
                        Register
                    </button>
                </div>
            </nav>

            <div className="login-container">
                <form onSubmit={handleSubmit} className="login-form">
                    <div className="form-group">
                        <input
                            type="email"
                            id="email"
                            placeholder="Email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="password"
                            id="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    {error && <p className="error">{error}</p>}
                    {success && <p className="success">{success}</p>}
                    <button type="submit" className="login-button">Login</button>
                </form>
            </div>
        </div>
    );
};

export default LoginPage;
