import React, { useState } from 'react';
import './LoginPage.css';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('/api/LoginPage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const result = await response.json();

            if (response.ok) {
                console.log('Login successful:', result);
                setSuccess('Login successful!');
                setError('');
                // Redirect or perform further actions, e.g., navigating to a dashboard
            } else {
                setError(result.error || 'Login failed. Please try again.');
                setSuccess('');
            }
        } catch (error) {
            console.error('Error:', error);
            setError('Something went wrong, please try again.');
            setSuccess('');
        }
    };

    return (
        <>
            <div className="navbar">
                <h1>Zenith Bank</h1>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Email"
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password"
                />
                <button className="login-button" onClick={handleSubmit}>Login</button>
                <a href="/create-user" className="create-user-link">Create User</a>
            </div>
            <div className="content">
                {error && <p className="message">{error}</p>}
                {success && <p className="message">{success}</p>}
            </div>
        </>
    );
};

export default LoginPage;
