import React, { createContext, useContext, useState } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [user, setUser] = useState(null); // State for user information

    const login = async (email, password) => {
        // Fetch request to login
        const response = await fetch('/api/LoginPage', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email, password }),
        });

        const result = await response.json();

        if (response.ok) {
            setUser({ firstName: result.firstName, lastName: result.lastName });
            setIsAuthenticated(true);
        }
    };

    return (
        <AuthContext.Provider value={{ isAuthenticated, login, user }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
