import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext'; // Import useAuth

function Home() {
    const { user, logout } = useAuth(); // Access user and logout function from Auth context

    return (
        <div>
            <h1>Welcome to Zenith Bank</h1>
            <Link to="/create-account">
                <button>Create New Account</button>
            </Link>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                {/* Display user name if authenticated */}
                {user.isAuthenticated && (
                    <h2 style={{ textAlign: 'left' }}>
                        Hello, {user.first_name} {user.last_name}
                    </h2>
                )}

                {/* Logout button on the top right */}
                {user.isAuthenticated && (
                    <button
                        onClick={logout}
                        style={{
                            backgroundColor: '#ff4d4d',
                            color: 'white',
                            border: 'none',
                            padding: '8px 16px',
                            cursor: 'pointer',
                            borderRadius: '5px'
                        }}
                    >
                        Logout
                    </button>
                )}
            </div>

            <Link to="/TransactionLogs">
                <button>View Transaction Logs</button>
            </Link>
        </div>
    );
}

export default Home;
