import React, { useState } from 'react';
import './LoginPage.css';

const LoginPage = () => {
  const [name, setName] = useState(''); // Changed from username to name
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [user, setUser] = useState(null); // Store logged-in user

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/api/LoginPage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, password }), // Send name and password
      });

      const result = await response.json();

      if (response.ok) {
        console.log('Login successful:', result);
        setUser(result.user); // Set the user data upon successful login
        // Redirect or perform further actions, e.g., navigating to a dashboard
      } else {
        setError(result.error); // Show the error message
      }
    } catch (error) {
      console.error('Error:', error);
      setError('Something went wrong, please try again.');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p className="error">{error}</p>}
        <button type="submit">Login</button>
      </form>

      {user && (
        <div className="user-info">
          <h3>Welcome, {user.name}!</h3>
          <p>Email: {user.email}</p>
          <p>Address: {user.address}</p>
        </div>
      )}
    </div>
  );
};

export default LoginPage;
