import React, { useEffect, useState } from 'react';
import axios from 'axios';

function TransactionLogs({ userID }) {
  const [logs, setLogs] = useState([]);
  const [selectedLog, setSelectedLog] = useState(null);

  useEffect(() => {
    const fetchLogs = async () => {
      try {
        const response = await axios.get(`/api/transaction_logs?userID=${userID}`);
        const logsData = response.data.map(log => ({
          ...log,
          Date: new Date(log.Date)  // Convert string to Date object
        }));
        setLogs(logsData);
      } catch (error) {
        console.error('Error fetching transaction logs:', error);
      }
    };

    fetchLogs();
  }, [userID]);

  return (
    <div>
      <h1>Transaction Logs</h1>
      <ul>
        {logs.map((log, index) => (
          <li key={index} onClick={() => setSelectedLog(log)}>
            <strong>Amount:</strong> {log.Amount}, <strong>Date:</strong> {log.Date.toLocaleDateString()}
          </li>
        ))}
      </ul>

      {selectedLog && (
        <div>
          <h2>Transaction Details</h2>
          <p><strong>Amount:</strong> {selectedLog.Amount}</p>
          <p><strong>Date:</strong> {selectedLog.Date.toLocaleDateString()}</p>
          <p><strong>Description:</strong> {selectedLog.Description}</p>
        </div>
      )}
    </div>
  );
}

export default TransactionLogs;
