// src/components/NavBar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext'; // Importing AuthContext

const NavBar = () => {
    const { user } = useAuth(); // Access user data from context

    return (
        <nav style={styles.nav}>
            <div style={styles.navLeft}>
                <Link to="/" style={styles.link}>Zenith Bank</Link>
                <Link to="/create-account" style={styles.link}>Create Account</Link>
                <Link to="/TransactionLogs" style={styles.link}>Transaction Logs</Link>
            </div>
            <div style={styles.navRight}>
                {user && (
                    <span style={styles.userName}>
                        {user.firstName} {user.lastName}
                    </span>
                )}
            </div>
        </nav>
    );
};

const styles = {
    nav: {
        display: 'flex',
        justifyContent: 'space-between',
        padding: '10px',
        backgroundColor: '#1a1a1a',
        color: 'white',
    },
    navLeft: {
        display: 'flex',
    },
    navRight: {
        alignSelf: 'center',
    },
    link: {
        color: 'white',
        marginRight: '15px',
        textDecoration: 'none',
    },
    userName: {
        fontWeight: 'bold',
    },
};

export default NavBar;
