// App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './AuthContext';
import Home from './Home';
import CreateAccount from './CreateAccount';
import TransactionLogs from './TransactionLogs';
import LoginPage from './LoginPage';
import CreateUser from './CreateUser';

const ProtectedRoute = ({ children }) => {
    const { isAuthenticated } = useAuth();

    if (!isAuthenticated) {
        return <Navigate to="/login-page" />;
    }

    return children;
};

function App() {
    return (
        <AuthProvider>
            <Router>
                <div className="App">
                    <Routes>
                        {/* Route for the Home page */}
                        <Route path="/" element={<ProtectedRoute><Home /></ProtectedRoute>} />

                        {/* Route for the Create Account page */}
                        <Route path="/create-account" element={<ProtectedRoute><CreateAccount /></ProtectedRoute>} />

                        {/* Route for the Transaction Logs Page */}
                        <Route path="/TransactionLogs" element={<ProtectedRoute><TransactionLogs /></ProtectedRoute>} />

                        {/* Route for Login Page */}
                        <Route path="/login-page" element={<LoginPage />} />

                        {/* Route for Create User Page */}
                        <Route path="/create-user" element={<CreateUser />} />
                    </Routes>
                </div>
            </Router>
        </AuthProvider>
    );
}

export default App;
