import React, { useState } from 'react';
import './LoginPage.css';

const LoginPage = () => {
  const [name, setName] = useState(''); // Changed from username to name
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/api/LoginPage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, password }), // Changed from username to name
      });

      const result = await response.json();

      if (response.ok) {
        console.log('Login successful:', result);
        // Handle successful login, e.g., redirect or set user state
      } else {
        setError(result.error);
        console.error('Login failed:', result.error);
      }
    } catch (error) {
      console.error('Error:', error);
      setError('Something went wrong, please try again.');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label> {/* Changed from username to name */}
          <input
            type="text"
            id="name" // Changed from username to name
            value={name} // Changed from username to name
            onChange={(e) => setName(e.target.value)} // Changed from setUsername to setName
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p className="error">{error}</p>}
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
