import React, { useState } from 'react';
import './LoginPage.css';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [user, setUser] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('/api/LoginPage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const result = await response.json();

            if (response.ok) {
                setUser(result.user);
                setSuccess('Login successful!'); // Set success message
                setError(''); // Clear error message
            } else {
                // Handle specific error messages based on the server response
                if (result.error === 'Account does not exist') {
                    setError('Account does not exist. Please check your email.');
                } else if (result.error === 'Invalid password') {
                    setError('Invalid password. Please try again.');
                } else {
                    setError(result.error); // Handle any other errors
                }
                setSuccess(''); // Clear success message
            }
        } catch (error) {
            console.error('Error:', error);
            setError('Something went wrong, please try again.');
            setSuccess(''); // Clear success message
        }
    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            <form onSubmit={handleSubmit} className="login-form">
                <div className="form-group">
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        placeholder="Email"
                    />
                </div>
                <div className="form-group">
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        placeholder="Password"
                    />
                </div>
                {error && <p className="error">{error}</p>} {/* Display error message */}
                {success && <p className="success">{success}</p>} {/* Display success message */}
                <button type="submit" className="login-button">Login</button>
            </form>
            <p>Don't have an account? <a className="link" href="/create-user">Sign up now!</a></p>

            {user && (
                <div className="user-info">
                    <h3>Welcome, {user.name}!</h3>
                    <p>Email: {user.email}</p>
                    <p>Address: {user.address}</p>
                </div>
            )}
        </div>
    );
};

export default LoginPage;
