import React, { useState } from 'react';
import './LoginPage.css';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('/api/LoginPage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const result = await response.json();

            if (response.ok) {
                setMessage('Login successful!');
                // Redirect or perform further actions, e.g., navigating to a dashboard
            } else {
                setMessage(result.error || 'Invalid email or password');
            }
        } catch (error) {
            console.error('Error:', error);
            setMessage('Something went wrong, please try again.');
        }
    };

    return (
        <div className="login-page">
            <div className="register-message">Don't have an account? Register</div>
            <nav className="navbar">
                <a href="/" className="navbar-brand">Zenith Bank</a>
                <div className="navbar-links">
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <button type="submit" className="login-button" onClick={handleSubmit}>Login</button>
                </div>
            </nav>
            <div className="message">{message}</div>
        </div>
    );
};

export default LoginPage;
