import React, { useState, useEffect } from 'react';
import './LoginPage.css';

const LoginPage = () => {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [users, setUsers] = useState([]); // State to hold user data

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/api/LoginPage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, password }),
      });

      const result = await response.json();

      if (response.ok) {
        console.log('Login successful:', result);
        // Handle successful login, e.g., redirect or set user state
      } else {
        setError(result.error);
        console.error('Login failed:', result.error);
      }
    } catch (error) {
      console.error('Error:', error);
      setError('Something went wrong, please try again.');
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await fetch('/api/users'); // Fetch all users
      const data = await response.json();
      setUsers(data); // Update the state with fetched user data
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  useEffect(() => {
    fetchUsers(); // Call fetchUsers on component mount
  }, []);

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p className="error">{error}</p>}
        <button type="submit">Login</button>
      </form>

      {/* Display fetched user data */}
      <h3>Users in Database:</h3>
      <ul>
        {users.map((user) => (
          <li key={user._id}>{user.name} - {user.email}</li>
        ))}
      </ul>
    </div>
  );
};

export default LoginPage;
