// src/App.js

import React from 'react';
import { BrowserRouter as Router, Route, Routes, Redirect } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';  // Import AuthProvider
import Home from './components/Home';
import CreateAccount from './components/CreateAccount';
import TransactionLogs from './components/TransactionLogs';
import LoginPage from './components/LoginPage';
import CreateUser from './components/CreateUser';
import ProtectedRoute from './components/ProtectedRoute';  // Import ProtectedRoute

const App = () => {
    return (
        <AuthProvider>
            <Router>
                <div className="App">
                    <Routes>
                        <Route path="/login-page" element={<LoginPage />} />
                        <Route path="/create-user" element={<CreateUser />} />
                        <Route path="/" element={<ProtectedRoute component={Home} />} />  {/* Protect the Home route */}
                        <Route path="/create-account" element={<ProtectedRoute component={CreateAccount} />} />
                        <Route path="/transaction-logs" element={<ProtectedRoute component={TransactionLogs} />} />
                    </Routes>
                </div>
            </Router>
        </AuthProvider>
    );
};

export default App;
